package dayq;

public class day3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(3 < 4);
		System.out.println(3 > 2);
		System.out.println(3 >= 2);
		System.out.println(3 <= 2);
		System.out.println(3 == 2);
		System.out.println(3 != 2);
		
		
		
		System.out.println(10 == 10 && 11 == 11);
		System.out.println(10 != 10 && 11 == 11);
		System.out.println(10 == 10 && 11 != 11);
		System.out.println(10 != 10 && 11 != 11);
		
		System.out.println(4 == 4 || 4 == 4);
		System.out.println(4 != 4 || 4 == 4);
		System.out.println(4 == 4 || 4 != 4);
		System.out.println(4 != 4 || 4 != 4);
		
		System.out.println(!(4 > 5));
	
		
	}

}
