package dayq;

public class day9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int i = 1;
		while(i<=3) {
			System.out.println(i);
			i++;
		}
		int k =10;
		while(k>=1) {
			System.out.println(k);
			k --;
		}
		int l = 30;
		while(l >=3) {
			System.out.println(l);
			l = l-3;
		}
		
		
		
		int j =2;
		while(j<=20) {
			System.out.println(j);
			j = j+2;
		}
		
		int p = 50;
		while (p>=5) {
			System.out.println(p);
			p = p-5;
		}
		
		
	}
	

}
